# coding:utf-8


import requests
import json
from lxml import etree

headers = {
    'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/78.0.3904.108 Safari/537.36'}


# r = requests.get("http://www.bilibili.com/video/av80021406", headers=headers)
def getSearchPage(name, page):
    url = f"https://api.bilibili.com/x/web-interface/search/all/v2?context=&page={page}&order=&keyword={name}"
    r = requests.get(url, headers=headers)
    r_json = json.loads(r.text)

    num_page = r_json['data']['numPages']
    print('num_page ' + str(num_page))

    for i in range(0, 20):
        title = r_json['data']['result'][8]['data'][i]['title']
        arcurl = r_json['data']['result'][8]['data'][i]['arcurl']
        # author = r_json['data']['result'][8]['data'][i]['author']
        # print('author:' + author + ' title:' + title + ' arcurl:' + arcurl)
        print(str(i) + ' title:[' + title + '] arcurl:' + arcurl)


def get_page_html(page):
    url = f'https://maoyan.com/board/{page}'
    r = requests.get(url, headers=headers)
    html = etree.HTML(r.text)
    # //*[@id="app"]/div/div/div[1]/dl/dd[1]/div
    names = html.xpath('//*[@id="app"]/div/div/div[1]/dl/dd[1]/div')
    # print(dir(xpath))
    print(names)



    return r.text


def testRemoteVerfy():
    """
    测试打码是否可用
    :return:
    """
    import requests
    import time

    starttime = time.time()
    rsp = requests.post(url="http://10.0.0.4:8000/verify/base64/",
                        data={
                            'imageFile': ''},
                        timeout=60,
                        )
    print(str(rsp.content,'utf-8'))
    print(f"响应时间{time.time() - starttime}m")


testRemoteVerfy()


